//
// Allow non javascript and typescript files to be imported
// --------------------------------------------------------
declare module "*.scss" {
    const _: any;
    export default _;
}

declare module "*.css" {
    const _: any;
    export default _;
}

declare module "*.svg" {
    const _: any;
    export default _;
}

declare module "*.jpg" {
    const _: any;
    export default _;
}

declare module "*.jpeg" {
    const _: any;
    export default _;
}

declare module "*.gif" {
    const _: any;
    export default _;
}

declare module "*.png" {
    const _: any;
    export default _;
}

declare module "*.json" {
    const _: any;
    export default _;
}
